import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import Lottie from 'react-lottie'
import animationData from './nameSig.json'
import { create } from '@lottiefiles/lottie-interactivity';

export default function NavBar(props) {
    const [doctitle, setDocTitle] = useState("Lizzy's")
    useEffect(() => {
        document.title = doctitle
    }, [doctitle])

    NavBar.myRef = React.createRef()

    create({
        mode: 'scroll',
        player: '#firstLottie',
        actions: [
          {
            visibility: [0, 1],
            type: 'seek',
            frames: [0, 100],
          },
        ],
      });

    const defaultOptions = {
        loop: false,
        autoplay: true,
        animationData: animationData,
        rendererSettings: {
          preserveAspectRatio: 'xMidYMid slice'
        }
      };

    return (
        <nav>
             <Lottie options={defaultOptions}
              height={100}
              width={100}
              id="firstLottie"
            />  

            <ul>
                <li>
                    <Link 
                        to="/" 
                        onClick={() => setDocTitle("Home")}
                    >Home</Link>
                </li>
                <li>
                    <Link 
                        to="/about"
                        onClick={() => setDocTitle("About")}
                    >About</Link>
                </li>
                <li>
                    <Link 
                        to="/blog"
                        onClick={() => setDocTitle("Blog")}
                    >Blog</Link>
                </li>
                <li>
                    <Link 
                        to="/contact"
                        onClick={() => setDocTitle("Contact me!")}
                    >Contact</Link>
                </li>
                <li>
                    <Link to="/gallery"
                    onClick={() => setDocTitle("Gallery")}
                    >Gallery</Link>
                </li>
            </ul>
        </nav>
    )
}
